def create_resource(record)
  resource = Resource.create!(record['resource'])
  record['audiences'].each do |audience|
    resource.audiences.create!({:type => audience, :created_at => resource.created_at, :updated_at => resource.updated_at})
  end
  record['availabilities'].each do |availability|
    resource.availabilities.create!({:type => availability, :created_at => resource.created_at, :updated_at => resource.updated_at})
  end
  record['campuses'].each do |campus|
    resource.campuses.create!({:name => campus, :created_at => resource.created_at, :updated_at => resource.updated_at})
  end
  record['client_tags'].each do |client_tag |
    resource.client_tags.create!(:name => client_tag, :created_at => resource.created_at, :updated_at => resource.updated_at)
  end
  record['colleges'].each do |college|
    resource.colleges.create!(:name => college, :created_at => resource.created_at, :updated_at => resource.updated_at)
  end
  record['innovation_stages'].each do |innovation_stage|
    resource.innovation_stages.create!(:name => innovation_stage, :created_at => resource.created_at, :updated_at => resource.updated_at)
  end
  record['population_focuses'].each do |population_focus|
    resource.population_focuses.create!(:type => population_focus, :created_at => resource.created_at, :updated_at => resource.updated_at)
  end
  record['technologies'].each do |technology|
    resource.technologies.create!(:type => technology, :created_at => resource.created_at, :updated_at => resource.updated_at)
  end
  record['topics'].each do |topic|
    resource.topics.create!(:name => topic, :created_at => resource.created_at, :updated_at => resource.updated_at)
  end
  record['types'].each do |type|
    resource.types.create!(:name => type, :created_at => resource.created_at, :updated_at => resource.updated_at)
  end
end

